import React from "react";
import "./App.css";

const randomColor = () => {
  let str = "0123456789abcdef";
  let color = "";
  for (let i = 0; i < 6; i++) {
    let index = Math.floor(Math.random() * str.length);
    color += str[index];
  }
  return "#" + color;
};
// const colorStyle = {
//   background: result
// };
const color = ["#c2792f", "#75eca1", "#7923be", "#cc9640", "#eca4c8"];

const RandomColor = props => {
  const { color } = props;
  console.log(color);
  return (
    <div>
      <div className="color__card">{/* {result} */}</div>;
      <div className="color__card">{/* {result} */}</div>;
      <div className="color__card">{/* {result} */}</div>;
      <div className="color__card">{/* {result} */}</div>;
      <div className="color__card">{/* {result} */}</div>;
    </div>
  );
};

const App = () => {
  return (
    <div className="App">
      <RandomColor color={color} />
    </div>
  );
};

export default App;
