import React, { useState } from "react";
import "./App.css";

const randomColor = () => {
  let str = "0123456789abcdef";
  let color = "";
  for (let i = 0; i < 6; i++) {
    let index = Math.floor(Math.random() * str.length);
    color += str[index];
  }
  return "#" + color;
};

const RandomColor = () => {
  const [result] = useState(randomColor());
  // const result = randomColor();
  const colorStyle = {
    background: result
  };
  console.log(result, colorStyle);
  return (
    <div style={colorStyle} className="color__card">
      {console.log(result, colorStyle)}
      {result}
    </div>
  );
};

const App = () => {
  return (
    <div className="App">
      <RandomColor />
      {/* <RandomColor />
      <RandomColor />
      <RandomColor /> */}
    </div>
  );
};

export default App;
